import React, { useState } from 'react';

const ExpenseTracker = () => {
  const [date, setDate] = useState('');
  const [expenses, setExpenses] = useState({});
  const [totalExpenses, setTotalExpenses] = useState(0);
  const [category, setCategory] = useState('Food'); // Default category
  const [customCategory, setCustomCategory] = useState('');
  const [editMode, setEditMode] = useState(false);
  const [editExpenseIndex, setEditExpenseIndex] = useState(null);
  const [editAmount, setEditAmount] = useState(0);
  const [editCategory, setEditCategory] = useState('Food');

  const handleDateChange = (event) => {
    setDate(event.target.value);
  };

  const handleAddExpense = (event) => {
    event.preventDefault();
    const form = event.target;
    const amount = parseFloat(form.amount.value);
    const categoryToUse = category === 'Other' ? customCategory : category;

    if (date && amount && categoryToUse) {
      const newExpenses = { ...expenses };
      if (!newExpenses[date]) {
        newExpenses[date] = [];
      }
      if (editMode) {
        // Edit existing expense
        newExpenses[date][editExpenseIndex] = { amount, category: categoryToUse };
        setEditMode(false);
        setEditExpenseIndex(null);
        setEditAmount(0);
        setEditCategory('Food');
      } else {
        // Add new expense
        newExpenses[date].push({ amount, category: categoryToUse });
      }

      setExpenses(newExpenses);
      setTotalExpenses((prevTotal) => {
        const previousTotal = editMode ? totalExpenses - expenses[date][editExpenseIndex].amount : prevTotal;
        return previousTotal + amount;
      });

      form.reset(); // Reset form fields
      setCategory('Food'); // Reset category to default
      setCustomCategory(''); // Reset custom category
    }
  };

  const handleEditExpense = (date, index) => {
    const expenseToEdit = expenses[date][index];
    setEditMode(true);
    setEditExpenseIndex(index);
    setEditAmount(expenseToEdit.amount);
    setEditCategory(expenseToEdit.category);
  };

  const handleNextDate = () => {
    setDate('');
    setExpenses({ ...expenses }); // Keep previous expenses for the next date
  };

  // Calculate daily totals
  const dailyTotals = Object.entries(expenses).map(([date, expenseList]) => {
    const dailyTotal = expenseList.reduce((sum, expense) => sum + expense.amount, 0);
    return { date, total: dailyTotal, expenseList };
  });

  return (
    <div className="container">
      <h2 className="text-center mb-4">Expense Tracker</h2>

      <div className="mb-4">
        <input
          type="date"
          value={date}
          onChange={handleDateChange}
          className="form-control"
        />
      </div>

      {date && (
        <form onSubmit={handleAddExpense}>
          <div className="mb-3">
            <input
              type="number"
              name="amount"
              placeholder="Expense Amount"
              value={editMode ? editAmount : ''}
              onChange={(e) => editMode && setEditAmount(e.target.value)}
              required
              className="form-control"
            />
          </div>
          <div className="mb-3">
            <select
              className="form-select"
              value={editMode ? editCategory : category}
              onChange={(e) => {
                if (editMode) {
                  setEditCategory(e.target.value);
                } else {
                  setCategory(e.target.value);
                  if (e.target.value !== 'Other') {
                    setCustomCategory(''); // Clear custom category if not 'Other'
                  }
                }
              }}
            >
              <option value="Food">Food</option>
              <option value="Accommodation">Accommodation</option>
              <option value="Travel">Travel</option>
              <option value="Other">Other</option>
            </select>
          </div>
          {editMode && editCategory === 'Other' && (
            <div className="mb-3">
              <input
                type="text"
                value={customCategory}
                onChange={(e) => setCustomCategory(e.target.value)}
                placeholder="Specify Other Category"
                className="form-control"
                required
              />
            </div>
          )}
          {!editMode && category === 'Other' && (
            <div className="mb-3">
              <input
                type="text"
                value={customCategory}
                onChange={(e) => setCustomCategory(e.target.value)}
                placeholder="Specify Other Category"
                className="form-control"
                required
              />
            </div>
          )}
          <button type="submit" className="btn btn-primary">
            {editMode ? 'Update Expense' : 'Add Expense'}
          </button>
        </form>
      )}

      {dailyTotals.length > 0 && (
        <div className="mt-4">
          <h4 className="text-center">Daily Expense Summary</h4>
          <ul className="list-group">
            {dailyTotals.map(({ date, total, expenseList }, index) => (
              <li key={index} className="list-group-item">
                <h5>{date}: Total Expenses = ₹{total.toFixed(2)}</h5>
                <ul>
                  {expenseList.map((expense, expenseIndex) => (
                    <li key={expenseIndex}>
                      {expense.category}: ₹{expense.amount.toFixed(2)}
                      <button
                        className="btn btn-link"
                        onClick={() => handleEditExpense(date, expenseIndex)}
                      >
                        Edit
                      </button>
                    </li>
                  ))}
                </ul>
              </li>
            ))}
          </ul>
        </div>
      )}

      <div className="mt-4 text-center">
        <h5>Total Expenses: ₹{totalExpenses.toFixed(2)}</h5>
        <button className="btn btn-secondary" onClick={handleNextDate}>
          Next Date
        </button>
      </div>
    </div>
  );
};

export default ExpenseTracker;
