import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const ExpenseTracker = ({ expenses, setExpenses }) => {
  const [description, setDescription] = useState('');
  const [amount, setAmount] = useState('');
  const navigate = useNavigate();

  const addExpense = () => {
    setExpenses([...expenses, { description, amount: parseFloat(amount) }]);
    setDescription('');
    setAmount('');
  };

  const goToBudget = () => {
    navigate('/budget');
  };

  return (
    <div>
      <h2 className="text-center">Expense Tracker</h2>
      <div className="mb-3">
        <input
          className="form-control"
          type="text"
          placeholder="Expense Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
      </div>
      <div className="mb-3">
        <input
          className="form-control"
          type="number"
          placeholder="Amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
        />
      </div>
      <button className="btn btn-primary" onClick={addExpense}>Add Expense</button>

      <h3 className="mt-4">Expenses</h3>
      <ul className="list-group">
        {expenses.map((expense, index) => (
          <li key={index} className="list-group-item">
            {expense.description} - â‚¹{expense.amount}
          </li>
        ))}
      </ul>

      <button className="btn btn-secondary mt-3" onClick={goToBudget}>Go to Budget Page</button>
    </div>
  );
};

export default ExpenseTracker;
