import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import jsPDF from 'jspdf';
import 'jspdf-autotable'; // Import jsPDF AutoTable plugin for better table formatting

const Home = () => {
  const [country, setCountry] = useState('');
  const [state, setState] = useState('');
  const [cities, setCities] = useState([{ city: '', places: '', totalBudget: '' }]);
  const [expensePlan, setExpensePlan] = useState(null);

  // Predefined percentages for budget allocation
  const budgetPercentages = {
    transport: 0.3,
    food: 0.3,
    activities: 0.4
  };

  // Function to add a new city with its places
  const handleAddCity = () => {
    setCities([...cities, { city: '', places: '', totalBudget: '' }]);
  };

  // Function to handle input changes
  const handleCityChange = (index, event) => {
    const newCities = [...cities];
    newCities[index][event.target.name] = event.target.value;
    setCities(newCities);
  };

  // Function to generate the expense planner
  const handleGeneratePlanner = () => {
    const plan = cities.map(cityObj => {
      const totalBudget = parseFloat(cityObj.totalBudget || 0);
      const transportBudget = (totalBudget * budgetPercentages.transport).toFixed(2);
      const foodBudget = (totalBudget * budgetPercentages.food).toFixed(2);
      const activitiesBudget = (totalBudget * budgetPercentages.activities).toFixed(2);

      const placesArray = cityObj.places.split(',').map(place => place.trim());
      const perPlaceBudget = (totalBudget / placesArray.length).toFixed(2);

      // Calculate separate budgets for each place
      const placesBudgetDetails = placesArray.map(place => ({
        name: place,
        totalPlaceBudget: perPlaceBudget,
        transportBudget: (perPlaceBudget * budgetPercentages.transport).toFixed(2),
        foodBudget: (perPlaceBudget * budgetPercentages.food).toFixed(2),
        activitiesBudget: (perPlaceBudget * budgetPercentages.activities).toFixed(2)
      }));

      return {
        city: cityObj.city,
        totalBudget: totalBudget.toFixed(2),
        transportBudget,
        foodBudget,
        activitiesBudget,
        places: placesBudgetDetails
      };
    });

    setExpensePlan(plan);
  };

  // Function to download the PDF
  const handleDownloadPDF = () => {
    const doc = new jsPDF();

    // Set font style and size
    doc.setFont("helvetica", "normal");
    doc.setFontSize(14);
    doc.text("Expense Planner", 14, 20);

    // Set initial Y position for text
    let yPosition = 30;

    if (expensePlan) {
      expensePlan.forEach(cityObj => {
        doc.setFontSize(12);
        doc.text(`${cityObj.city} - Total Budget: ₹${cityObj.totalBudget}`, 14, yPosition);
        yPosition += 10;

        // Prepare data for AutoTable
        const tableData = cityObj.places.map(place => [
          place.name,
          `₹${place.totalPlaceBudget}`,
          `₹${place.transportBudget}`,
          `₹${place.foodBudget}`,
          `₹${place.activitiesBudget}`
        ]);

        // Add table for places
        doc.autoTable({
          head: [['Place Name', 'Total Budget', 'Transport', 'Food', 'Activities']],
          body: tableData,
          startY: yPosition,
          theme: 'grid', // Grid theme for better readability
          styles: {
            cellPadding: 4,
            fontSize: 10,
            overflow: 'linebreak',
            lineColor: [44, 62, 80],
            fillColor: [255, 255, 255],
            textColor: [0, 0, 0],
            halign: 'left',
            valign: 'middle'
          },
          headStyles: {
            fillColor: [52, 152, 219], // Light blue for headers
            textColor: [255, 255, 255],
            fontSize: 12,
            halign: 'center'
          },
          didParseCell: (data) => {
            if (data.row.index === 0) {
              data.cell.styles.fillColor = [52, 152, 219]; // Header background color
            }
          },
        });

        // Update Y position after the table
        yPosition = doc.lastAutoTable.finalY + 10; // Add extra space after the table
      });
    }

    // Save the PDF
    doc.save("expense_planner.pdf");
  };

  return (
    <div className="text-center">
      <h1>Welcome to Travel Toes</h1>
      <p>Your ultimate expense tracker and budget management tool.</p>

      {/* Input form */}
      <div className="input-form">
        <input 
          type="text" 
          placeholder="Country" 
          value={country} 
          onChange={(e) => setCountry(e.target.value)} 
          className="form-control m-2"
        />
        <input 
          type="text" 
          placeholder="State" 
          value={state} 
          onChange={(e) => setState(e.target.value)} 
          className="form-control m-2"
        />

        {/* Cities input */}
        {cities.map((cityObj, index) => (
          <div key={index} className="city-input">
            <input 
              type="text" 
              name="city"
              placeholder="City" 
              value={cityObj.city} 
              onChange={(e) => handleCityChange(index, e)} 
              className="form-control m-2"
            />
            <input 
              type="text" 
              name="places"
              placeholder="List of Places (comma-separated)" 
              value={cityObj.places} 
              onChange={(e) => handleCityChange(index, e)} 
              className="form-control m-2"
            />
            <input 
              type="number" 
              name="totalBudget"
              placeholder="Total Budget" 
              value={cityObj.totalBudget} 
              onChange={(e) => handleCityChange(index, e)} 
              className="form-control m-2"
            />
          </div>
        ))}

        <button className="btn btn-info m-2" onClick={handleAddCity}>
          Add Another City
        </button>

        <button className="btn btn-success m-2" onClick={handleGeneratePlanner}>
          Generate Expense Planner
        </button>
      </div>

      {/* Display the generated expense plan */}
      {expensePlan && (
        <div className="expense-plan mt-4">
          <h3>Expense Planner</h3>
          {expensePlan.map((cityObj, index) => (
            <div key={index} className="city-expense mb-4">
              <h4>{cityObj.city} - Total Budget: ₹{cityObj.totalBudget}</h4>

              {/* Breakdown of budget for each place */}
              {cityObj.places.map((place, i) => (
                <div key={i} className="place-expense">
                  <h5>
                    {place.name} - Total Budget for Place: ₹{place.totalPlaceBudget}
                  </h5>
                  <p>Transport: ₹{place.transportBudget}</p>
                  <p>Food: ₹{place.foodBudget}</p>
                  <p>Activities: ₹{place.activitiesBudget}</p>
                </div>
              ))}
            </div>
          ))}

          {/* Add spacing below the generated planner */}
          <div className="spacer" style={{ marginBottom: '50px' }}></div>

          {/* Download PDF button */}
          <button className="btn btn-primary m-2" onClick={handleDownloadPDF}>
            Download PDF
          </button>
        </div>
      )}

      {/* Add space between the generated planner and buttons */}
      <div className="navigation-buttons mt-5">
        <Link to="/expense-tracker" className="btn btn-primary m-2">
          Go to Expense Tracker
        </Link>
        <Link to="/budget" className="btn btn-secondary m-2">
          Go to Budget Management
        </Link>
      </div>
    </div>
  );
};

export default Home;
