import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
  const [country, setCountry] = useState('');
  const [state, setState] = useState('');
  const [budget, setBudget] = useState('');
  const [cities, setCities] = useState([{ city: '', places: '' }]);
  const [expensePlan, setExpensePlan] = useState(null);

  // Function to add a new city with its places
  const handleAddCity = () => {
    setCities([...cities, { city: '', places: '' }]);
  };

  // Function to handle city input changes
  const handleCityChange = (index, event) => {
    const newCities = [...cities];
    newCities[index][event.target.name] = event.target.value;
    setCities(newCities);
  };

  // Function to generate the expense planner
  const handleGeneratePlanner = () => {
    const totalCities = cities.length;
    const perCityBudget = budget / totalCities;

    const plan = cities.map(cityObj => {
      const placesArray = cityObj.places.split(',').map(place => place.trim());
      const perPlaceBudget = perCityBudget / placesArray.length;

      return {
        city: cityObj.city,
        places: placesArray.map(place => ({
          name: place,
          allocatedBudget: perPlaceBudget.toFixed(2)
        }))
      };
    });

    setExpensePlan(plan);
  };

  return (
    <div className="text-center">
      <h1>Welcome to Travel Toes</h1>
      <p>Your ultimate expense tracker and budget management tool.</p>

      {/* Input form */}
      <div className="input-form">
        <input 
          type="text" 
          placeholder="Country" 
          value={country} 
          onChange={(e) => setCountry(e.target.value)} 
          className="form-control m-2"
        />
        <input 
          type="text" 
          placeholder="State" 
          value={state} 
          onChange={(e) => setState(e.target.value)} 
          className="form-control m-2"
        />
        <input 
          type="number" 
          placeholder="Total Budget" 
          value={budget} 
          onChange={(e) => setBudget(e.target.value)} 
          className="form-control m-2"
        />

        {/* Cities input */}
        {cities.map((cityObj, index) => (
          <div key={index} className="city-input">
            <input 
              type="text" 
              name="city"
              placeholder="City" 
              value={cityObj.city} 
              onChange={(e) => handleCityChange(index, e)} 
              className="form-control m-2"
            />
            <input 
              type="text" 
              name="places"
              placeholder="List of Places (comma-separated)" 
              value={cityObj.places} 
              onChange={(e) => handleCityChange(index, e)} 
              className="form-control m-2"
            />
          </div>
        ))}

        <button className="btn btn-info m-2" onClick={handleAddCity}>
          Add Another City
        </button>

        <button className="btn btn-success m-2" onClick={handleGeneratePlanner}>
          Generate Expense Planner
        </button>
      </div>

      {/* Display the generated expense plan */}
      {expensePlan && (
        <div className="expense-plan mt-4">
          <h3>Temporary Expense Planner</h3>
          {expensePlan.map((cityObj, index) => (
            <div key={index} className="city-expense">
              <h4>{cityObj.city}</h4>
              <ul className="list-group">
                {cityObj.places.map((place, i) => (
                  <li key={i} className="list-group-item">
                    <strong>{place.name}</strong>: ${place.allocatedBudget}
                  </li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      )}

      <Link to="/expense-tracker" className="btn btn-primary m-2">Go to Expense Tracker</Link>
      <Link to="/budget" className="btn btn-secondary m-2">Go to Budget Management</Link>
    </div>
  );
};

export default Home;
