// src/pages/ExpenseTracker.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Pie } from 'react-chartjs-2'; // Ensure you have this package installed
import { Chart, ArcElement, CategoryScale } from 'chart.js';

// Register the necessary components for Chart.js
Chart.register(ArcElement, CategoryScale);

const ExpenseTracker = () => {
  const [expenses, setExpenses] = useState([]);
  const [description, setDescription] = useState('');
  const [amount, setAmount] = useState('');
  const [total, setTotal] = useState(0);
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    const fetchExpenses = async () => {
      const response = await axios.get('/api/expenses');
      setExpenses(response.data);
    };

    fetchExpenses();
  }, []);

  useEffect(() => {
    const totalExpenses = expenses.reduce((acc, expense) => acc + expense.amount, 0);
    setTotal(totalExpenses);

    // Set unique categories for the chart
    const uniqueCategories = [...new Set(expenses.map(expense => expense.category))];
    setCategories(uniqueCategories);
  }, [expenses]);

  const addExpense = async (e) => {
    e.preventDefault();
    if (!description || !amount) return;

    const newExpense = { description, amount: parseFloat(amount), category: "Others" }; // Default category
    await axios.post('/api/expenses', newExpense);
    setExpenses([...expenses, newExpense]);
    setDescription('');
    setAmount('');
  };

  // Prepare data for the Pie chart
  const chartData = {
    labels: categories,
    datasets: [{
      data: expenses.map(expense => expense.amount),
      backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#9966FF'],
    }]
  };

  return (
    <div>
      <h1>Expense Tracker</h1>
      <form onSubmit={addExpense}>
        <input
          type="text"
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
        <input
          type="number"
          placeholder="Amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
        />
        <button type="submit">Add Expense</button>
      </form>
      <h2>Total Expenses: ${total.toFixed(2)}</h2>
      <h3>Category-wise Breakdown</h3>
      {categories.length > 0 && (
        <Pie data={chartData} />
      )}
    </div>
  );
};

export default ExpenseTracker;
