import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const BudgetManagement = () => {
  const [budgets, setBudgets] = useState([]);
  const [category, setCategory] = useState('Food');
  const [customCategory, setCustomCategory] = useState('');
  const [budgetAmount, setBudgetAmount] = useState('');
  const [expenses, setExpenses] = useState([]);
  const [totalBudget, setTotalBudget] = useState(0);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [showCustomCategory, setShowCustomCategory] = useState(false);

  useEffect(() => {
    fetchExpenses();
    fetchBudgets();
  }, []);

  useEffect(() => {
    setShowCustomCategory(category === 'Other');
    if (category !== 'Other') {
      setCustomCategory('');
    }
  }, [category]);

  const fetchBudgets = async () => {
    setIsLoading(true);
    setError('');
    try {
      const token = localStorage.getItem('token');
      if (!token) throw new Error('No authentication token found');

      const response = await fetch('http://localhost:5000/api/budgets', {
        method: 'GET',
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to fetch budgets');
      }

      const data = await response.json();
      const processedBudgets = (data.budgets || []).map(budget => ({
        ...budget,
        category: budget.category || 'Unknown',
      }));

      setBudgets(processedBudgets);
      calculateTotalBudget(processedBudgets);
    } catch (error) {
      setError(`Error fetching budgets: ${error.message}`);
      console.error('Error fetching budgets:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const fetchExpenses = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) throw new Error('No authentication token found');

      const response = await fetch('http://localhost:5000/api/expenses', {
        method: 'GET',
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to fetch expenses');
      }

      const data = await response.json();
      setExpenses(data.expenses || []);
    } catch (error) {
      setError(`Error fetching expenses: ${error.message}`);
      console.error('Error fetching expenses:', error);
    }
  };

  const calculateTotalBudget = budgetsList => {
    const total = budgetsList.reduce(
      (sum, budget) => sum + parseFloat(budget.amount || 0),
      0
    );
    setTotalBudget(total);
  };

  const validateBudgetData = () => {
    if (!category) throw new Error('Please select a category');
    if (category === 'Other' && !customCategory.trim())
      throw new Error('Please enter a custom category name');
    const amount = parseFloat(budgetAmount);
    if (isNaN(amount) || amount <= 0)
      throw new Error('Please enter a valid budget amount greater than 0');
    return amount;
  };

  const handleSetBudget = async e => {
    e.preventDefault();
    setError('');
    setIsLoading(true);

    try {
      const amount = validateBudgetData();
      const token = localStorage.getItem('token');
      if (!token) throw new Error('No authentication token found');

      const actualCategory = category === 'Other' ? customCategory : category;
      const existingBudget = budgets.find(b => b.category === actualCategory);
      const method = existingBudget ? 'PUT' : 'POST';
      const url = existingBudget
        ? `http://localhost:5000/api/budgets/${existingBudget._id}`
        : 'http://localhost:5000/api/budgets';

      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ category: actualCategory, amount }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to set budget');
      }

      const data = await response.json();
      setBudgets(prev =>
        method === 'POST'
          ? [...prev, data.budget]
          : prev.map(b => (b.category === actualCategory ? { ...b, amount } : b))
      );

      calculateTotalBudget(budgets);
      setBudgetAmount('');
      if (category === 'Other') setCustomCategory('');
    } catch (error) {
      setError(`Error setting budget: ${error.message}`);
      console.error('Error setting budget:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleDeleteBudget = async categoryToDelete => {
    setIsLoading(true);
    setError('');

    try {
      const token = localStorage.getItem('token');
      if (!token) throw new Error('No authentication token found');

      const budgetToDelete = budgets.find(b => b.category === categoryToDelete);
      if (!budgetToDelete) throw new Error('Budget not found');

      const response = await fetch(
        `http://localhost:5000/api/budgets/${budgetToDelete._id}`,
        {
          method: 'DELETE',
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        }
      );

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to delete budget');
      }

      setBudgets(budgets.filter(b => b.category !== categoryToDelete));
      calculateTotalBudget(budgets);
    } catch (error) {
      setError(`Error deleting budget: ${error.message}`);
      console.error('Error deleting budget:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const calculateAmountSpent = budgetCategory =>
    expenses
      .filter(expense => expense.category === budgetCategory)
      .reduce((sum, expense) => sum + parseFloat(expense.amount || 0), 0);

  const calculateBalance = () =>
    totalBudget - expenses.reduce((sum, expense) => sum + parseFloat(expense.amount || 0), 0);

  const chartData = {
    labels: budgets.map(budget => budget.category),
    datasets: [
      {
        label: 'Budget Amount',
        data: budgets.map(budget => budget.amount || 0),
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
        borderColor: 'rgba(75, 192, 192, 1)',
        borderWidth: 1,
      },
      {
        label: 'Actual Spending',
        data: budgets.map(budget => calculateAmountSpent(budget.category)),
        backgroundColor: 'rgba(255, 99, 132, 0.6)',
        borderColor: 'rgba(255, 99, 132, 1)',
        borderWidth: 1,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: true,
    aspectRatio: 2,
    plugins: {
      legend: { position: 'top' },
      title: { display: true, text: 'Budget vs. Spending by Category' },
    },
    scales: {
      y: { beginAtZero: true },
    },
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Budget Management</h1>

      {error && <div className="alert alert-danger">{error}</div>}

      <div className="text-center mb-4">
        <h4>Total Trip Budget: ${totalBudget.toFixed(2)}</h4>
        <h4>Balance: ${calculateBalance().toFixed(2)}</h4>
      </div>

      <div className="row">
        <div className="col-md-5">
          <div className="card shadow-sm">
            <div className="card-body">
              <h5 className="card-title">Set Budget</h5>
              <form onSubmit={handleSetBudget}>
                <div className="mb-3">
                  <label className="form-label">Category:</label>
                  <select
                    className="form-select"
                    value={category}
                    onChange={e => setCategory(e.target.value)}
                    disabled={isLoading}
                  >
                    <option value="Food">Food</option>
                    <option value="Transport">Transport</option>
                    <option value="Shopping">Shopping</option>
                    <option value="Accommodation">Accommodation</option>
                    <option value="Entertainment">Entertainment</option>
                    <option value="Other">Other</option>
                  </select>
                </div>
                {showCustomCategory && (
                  <div className="mb-3">
                    <label className="form-label">Custom Category:</label>
                    <input
                      type="text"
                      className="form-control"
                      value={customCategory}
                      onChange={e => setCustomCategory(e.target.value)}
                      disabled={isLoading}
                    />
                  </div>
                )}
                <div className="mb-3">
                  <label className="form-label">Budget Amount ($):</label>
                  <input
                    type="number"
                    className="form-control"
                    value={budgetAmount}
                    onChange={e => setBudgetAmount(e.target.value)}
                    disabled={isLoading}
                  />
                </div>
                <button type="submit" className="btn btn-primary w-100" disabled={isLoading}>
                  {isLoading ? 'Loading...' : 'Set Budget'}
                </button>
              </form>
            </div>
          </div>
        </div>
        <div className="col-md-7">
          <div className="card shadow-sm">
            <div className="card-body">
              <h5 className="card-title">Budget Overview</h5>
              <Bar data={chartData} options={chartOptions} />
            </div>
          </div>
        </div>
      </div>

      <div className="table-responsive mt-4">
        <table className="table table-bordered">
          <thead>
            <tr>
              <th>Category</th>
              <th>Budget Amount ($)</th>
              <th>Amount Spent ($)</th>
              <th>Remaining ($)</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {budgets.map(budget => (
              <tr key={budget.category}>
                <td>{budget.category}</td>
                <td>{budget.amount}</td>
                <td>{calculateAmountSpent(budget.category).toFixed(2)}</td>
                <td>{(budget.amount - calculateAmountSpent(budget.category)).toFixed(2)}</td>
                <td>
                  <button
                    className="btn btn-danger btn-sm"
                    onClick={() => handleDeleteBudget(budget.category)}
                    disabled={isLoading}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div className="text-center mt-4">
        <Link to="/home" className="btn btn-secondary">
          Back to Home
        </Link>
      </div>
    </div>
  );
};

export default BudgetManagement;
