// Function to download the PDF
const handleDownloadPDF = () => {
  const doc = new jsPDF();
  doc.setFont("Helvetica", "normal");
  doc.setFontSize(16);
  doc.text("Expense Planner", 14, 20);

  let yPosition = 30;
  if (expensePlan) {
      expensePlan.forEach(cityObj => {
          doc.setFontSize(14);
          doc.text(`${cityObj.city} - Total Budget: ₹${cityObj.totalBudget}`, 14, yPosition);
          yPosition += 10;

          cityObj.places.forEach((place, i) => {
              doc.setFontSize(12);
              doc.text(`${place.name} - Total Budget for Place: ₹${place.totalPlaceBudget}`, 14, yPosition);
              yPosition += 6;
              doc.text(`Transport: ₹${place.transportBudget}`, 20, yPosition);
              yPosition += 6;
              doc.text(`Food: ₹${place.foodBudget}`, 20, yPosition);
              yPosition += 6;
              doc.text(`Activities: ₹${place.activitiesBudget}`, 20, yPosition);
              yPosition += 10; // Extra space before next place
          });
          yPosition += 10; // Extra space before next city
      });
  }

  doc.save("expense_planner.pdf");
};
