import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const BudgetManagement = () => {
  const [budgets, setBudgets] = useState([]);
  const [category, setCategory] = useState('Food');
  const [budgetAmount, setBudgetAmount] = useState('');
  const [expenses, setExpenses] = useState([]);
  const [totalBudget, setTotalBudget] = useState(0);
  const [error, setError] = useState('');

  const fetchBudgets = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/budgets', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        setBudgets(data.data);
        calculateTotalBudget(data.data);
      } else {
        const errorData = await response.json();
        setError(errorData.message);
      }
    } catch (error) {
      setError('Error fetching trip budgets');
      console.error('Error fetching trip budgets:', error);
    }
  };

  const fetchExpenses = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/expenses', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        setExpenses(data.expenses);
      }
    } catch (error) {
      console.error('Error fetching trip expenses:', error);
    }
  };

  useEffect(() => {
    fetchBudgets();
    fetchExpenses();
  }, []);

  const calculateTotalBudget = (budgetsList) => {
    const total = budgetsList.reduce((sum, budget) => sum + budget.amount, 0);
    setTotalBudget(total);
  };

  const handleSetBudget = async (e) => {
    e.preventDefault();
    if (!budgetAmount || isNaN(budgetAmount) || parseFloat(budgetAmount) <= 0) {
      setError('Please enter a valid budget amount');
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const existingBudget = budgets.find(b => b.category === category);

      if (existingBudget) {
        // Update existing budget
        const response = await fetch(`http://localhost:5000/api/budgets/${existingBudget._id}`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          },
          body: JSON.stringify({
            amount: parseFloat(budgetAmount)
          })
        });

        if (!response.ok) {
          throw new Error('Failed to update trip budget');
        }
      } else {
        // Create new budget
        const response = await fetch('http://localhost:5000/api/budgets', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          },
          body: JSON.stringify({
            category,
            amount: parseFloat(budgetAmount),
            startDate: new Date() // Current trip start date
          })
        });

        if (!response.ok) {
          throw new Error('Failed to create trip budget');
        }
      }

      // Refresh budgets
      fetchBudgets();
      setBudgetAmount('');
      setError('');
    } catch (error) {
      setError(error.message);
      console.error('Error setting trip budget:', error);
    }
  };

  const handleDeleteBudget = async (budgetId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5000/api/budgets/${budgetId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        fetchBudgets();
      } else {
        throw new Error('Failed to delete trip budget');
      }
    } catch (error) {
      setError(error.message);
      console.error('Error deleting trip budget:', error);
    }
  };

  const calculateAmountSpent = (category) => {
    return expenses
      .filter(expense => expense.category === category)
      .reduce((sum, expense) => sum + expense.amount, 0);
  };

  const calculateTotalSpent = () => {
    return expenses.reduce((sum, expense) => sum + expense.amount, 0);
  };

  const calculateBalance = () => {
    const totalSpent = calculateTotalSpent();
    return totalBudget - totalSpent;
  };

  // Calculate percentage of budget used for progress bars
  const calculatePercentageUsed = (category) => {
    const budget = budgets.find(b => b.category === category);
    if (!budget) return 0;
    const spent = calculateAmountSpent(category);
    return Math.min((spent / budget.amount) * 100, 100);
  };

  const chartData = {
    labels: budgets.map(budget => budget.category),
    datasets: [
      {
        label: 'Trip Budget Amount',
        data: budgets.map(budget => budget.amount),
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
        borderColor: 'rgba(75, 192, 192, 1)',
        borderWidth: 1,
      },
      {
        label: 'Current Spending',
        data: budgets.map(budget => calculateAmountSpent(budget.category)),
        backgroundColor: 'rgba(255, 99, 132, 0.6)',
        borderColor: 'rgba(255, 99, 132, 1)',
        borderWidth: 1,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: true,
    aspectRatio: 2,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Trip Budget vs. Current Spending by Category'
      },
    },
    scales: {
      y: {
        beginAtZero: true,
      },
    },
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Trip Budget Management</h1>

      {error && (
        <div className="alert alert-danger" role="alert">
          {error}
        </div>
      )}

      <div className="text-center mb-4">
        <h4>Total Trip Budget: ${totalBudget.toFixed(2)}</h4>
        <h4 className={calculateBalance() >= 0 ? 'text-success' : 'text-danger'}>
          Remaining Budget: ${calculateBalance().toFixed(2)}
        </h4>
      </div>

      <div className="row">
        <div className="col-md-5">
          <div className="card shadow-sm">
            <div className="card-body">
              <h5 className="card-title">Set Trip Budget</h5>
              <form onSubmit={handleSetBudget}>
                <div className="mb-3">
                  <label className="form-label">Category:</label>
                  <select 
                    className="form-select"
                    value={category}
                    onChange={(e) => setCategory(e.target.value)}
                  >
                    <option value="Food">Food</option>
                    <option value="Transport">Transport</option>
                    <option value="Accommodation">Accommodation</option>
                    <option value="Activities">Activities</option>
                    <option value="Shopping">Shopping</option>
                    <option value="Entertainment">Entertainment</option>
                    <option value="Other">Other</option>
                  </select>
                </div>

                <div className="mb-3">
                  <label className="form-label">Budget Amount:</label>
                  <input
                    type="number"
                    className="form-control"
                    value={budgetAmount}
                    onChange={(e) => setBudgetAmount(e.target.value)}
                    min="0"
                    step="0.01"
                    required
                  />
                </div>

                <div className="d-grid">
                  <button type="submit" className="btn btn-primary">
                    Set Budget
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>

        <div className="col-md-7">
          <div className="card shadow-sm">
            <div className="card-body">
              <h5 className="card-title">Budget Overview</h5>
              <div className="chart-container">
                <Bar data={chartData} options={chartOptions} />
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="card shadow-sm mt-4">
        <div className="card-body">
          <h5 className="card-title">Budget Details</h5>
          <div className="table-responsive">
            <table className="table">
              <thead>
                <tr>
                  <th>Category</th>
                  <th>Budget Amount</th>
                  <th>Amount Spent</th>
                  <th>Remaining</th>
                  <th>Progress</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {budgets.map(budget => {
                  const amountSpent = calculateAmountSpent(budget.category);
                  const remaining = budget.amount - amountSpent;
                  const percentageUsed = calculatePercentageUsed(budget.category);

                  return (
                    <tr key={budget._id}>
                      <td>{budget.category}</td>
                      <td>${budget.amount.toFixed(2)}</td>
                      <td>${amountSpent.toFixed(2)}</td>
                      <td className={remaining >= 0 ? 'text-success' : 'text-danger'}>
                        ${remaining.toFixed(2)}
                      </td>
                      <td>
                        <div className="progress">
                          <div 
                            className={`progress-bar ${remaining >= 0 ? 'bg-success' : 'bg-danger'}`}
                            role="progressbar" 
                            style={{ width: `${percentageUsed}%` }}
                            aria-valuenow={percentageUsed}
                            aria-valuemin="0" 
                            aria-valuemax="100"
                          >
                            {percentageUsed.toFixed(1)}%
                          </div>
                        </div>
                      </td>
                      <td>
                        <button
                          className="btn btn-danger btn-sm"
                          onClick={() => handleDeleteBudget(budget._id)}
                        >
                          Delete
                        </button>
                      </td>
                    </tr>
                  )})}
              </tbody>
            </table>
          </div>
        </div>
      </div>

      <div className="card shadow-sm mt-4 mb-4">
        <div className="card-body text-center">
          <h5 className="mb-3">Track your trip expenses:</h5>
          <Link to="/" className="btn btn-secondary">
            Go to Expense Tracker
          </Link>
        </div>
      </div>
    </div>
  );
};

export default BudgetManagement;
