import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
  const [country, setCountry] = useState('');
  const [state, setState] = useState('');
  const [budget, setBudget] = useState('');
  const [places, setPlaces] = useState('');
  const [expensePlan, setExpensePlan] = useState(null);

  const handleGeneratePlanner = () => {
    // Simulate expense planner generation
    const placesArray = places.split(',').map(place => place.trim());
    const totalPlaces = placesArray.length;
    const perPlaceBudget = budget / totalPlaces;

    const plan = placesArray.map(place => ({
      place: place,
      allocatedBudget: perPlaceBudget.toFixed(2)
    }));

    setExpensePlan(plan);
  };

  return (
    <div className="text-center">
      <h1>Welcome to Travel Toes</h1>
      <p>Your ultimate expense tracker and budget management tool.</p>

      {/* Input form */}
      <div className="input-form">
        <input 
          type="text" 
          placeholder="Country" 
          value={country} 
          onChange={(e) => setCountry(e.target.value)} 
          className="form-control m-2"
        />
        <input 
          type="text" 
          placeholder="State" 
          value={state} 
          onChange={(e) => setState(e.target.value)} 
          className="form-control m-2"
        />
        <input 
          type="number" 
          placeholder="Total Budget" 
          value={budget} 
          onChange={(e) => setBudget(e.target.value)} 
          className="form-control m-2"
        />
        <input 
          type="text" 
          placeholder="List of Places (comma-separated)" 
          value={places} 
          onChange={(e) => setPlaces(e.target.value)} 
          className="form-control m-2"
        />

        <button className="btn btn-success m-2" onClick={handleGeneratePlanner}>
          Generate Expense Planner
        </button>
      </div>

      {/* Display the generated expense plan */}
      {expensePlan && (
        <div className="expense-plan mt-4">
          <h3>Temporary Expense Planner</h3>
          <ul className="list-group">
            {expensePlan.map((item, index) => (
              <li key={index} className="list-group-item">
                <strong>{item.place}</strong>: ${item.allocatedBudget}
              </li>
            ))}
          </ul>
        </div>
      )}

      <Link to="/expense-tracker" className="btn btn-primary m-2">Go to Expense Tracker</Link>
      <Link to="/budget" className="btn btn-secondary m-2">Go to Budget Management</Link>
    </div>
  );
};

export default Home;
