import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { jsPDF } from 'jspdf';

const Home = () => {
  const [cities, setCities] = useState([{ city: '', places: [{ name: '', transportBudget: 0, foodBudget: 0, activitiesBudget: 0 }] }]);
  const [expensePlan, setExpensePlan] = useState([]);

  // Handle adding another city
  const handleAddCity = () => {
    setCities([...cities, { city: '', places: [{ name: '', transportBudget: 0, foodBudget: 0, activitiesBudget: 0 }] }]);
  };

  // Handle adding another place
  const handleAddPlace = (cityIndex) => {
    const updatedCities = [...cities];
    updatedCities[cityIndex].places.push({ name: '', transportBudget: 0, foodBudget: 0, activitiesBudget: 0 });
    setCities(updatedCities);
  };

  // Handle change for city and places
  const handleInputChange = (e, cityIndex, placeIndex) => {
    const { name, value } = e.target;
    const updatedCities = [...cities];

    if (placeIndex !== undefined) {
      updatedCities[cityIndex].places[placeIndex][name] = name === 'transportBudget' || name === 'foodBudget' || name === 'activitiesBudget' ? Number(value) : value;
    } else {
      updatedCities[cityIndex][name] = value;
    }
    setCities(updatedCities);
  };

  // Handle generating expense planner
  const handleGeneratePlanner = () => {
    const updatedExpensePlan = cities.map(cityObj => {
      const totalCityBudget = cityObj.places.reduce((total, place) => total + place.transportBudget + place.foodBudget + place.activitiesBudget, 0);
      return {
        city: cityObj.city,
        totalBudget: totalCityBudget,
        places: cityObj.places.map(place => ({
          name: place.name,
          totalPlaceBudget: place.transportBudget + place.foodBudget + place.activitiesBudget,
          transportBudget: place.transportBudget,
          foodBudget: place.foodBudget,
          activitiesBudget: place.activitiesBudget,
        })),
      };
    });
    setExpensePlan(updatedExpensePlan);
  };

  // Function to download the PDF
  const handleDownloadPDF = () => {
    const doc = new jsPDF();
    doc.setFont("Helvetica", "normal");
    doc.setFontSize(16);
    doc.text("Expense Planner", 14, 20);

    let yPosition = 30;
    if (expensePlan.length > 0) {
      expensePlan.forEach(cityObj => {
        doc.setFontSize(14);
        doc.text(`${cityObj.city} - Total Budget: ₹${cityObj.totalBudget}`, 14, yPosition);
        yPosition += 10;

        cityObj.places.forEach((place) => {
          doc.setFontSize(12);
          doc.text(`${place.name} - Total Budget for Place: ₹${place.totalPlaceBudget}`, 14, yPosition);
          yPosition += 6;
          doc.text(`Transport: ₹${place.transportBudget}`, 20, yPosition);
          yPosition += 6;
          doc.text(`Food: ₹${place.foodBudget}`, 20, yPosition);
          yPosition += 6;
          doc.text(`Activities: ₹${place.activitiesBudget}`, 20, yPosition);
          yPosition += 10; // Extra space before next place
        });
        yPosition += 10; // Extra space before next city
      });
    }

    doc.save("expense_planner.pdf");
  };

  return (
    <div className="text-center">
      <h1>Welcome to Travel Toes</h1>
      <p>Your ultimate expense tracker and budget management tool.</p>

      <div>
        <h2>Temporary Expense Planner</h2>
        {cities.map((city, cityIndex) => (
          <div key={cityIndex}>
            <input
              type="text"
              placeholder="Enter City"
              name="city"
              value={city.city}
              onChange={(e) => handleInputChange(e, cityIndex)}
            />
            <button onClick={() => handleAddPlace(cityIndex)}>Add Place</button>
            {city.places.map((place, placeIndex) => (
              <div key={placeIndex}>
                <input
                  type="text"
                  placeholder="Enter Place Name"
                  name="name"
                  value={place.name}
                  onChange={(e) => handleInputChange(e, cityIndex, placeIndex)}
                />
                <input
                  type="number"
                  placeholder="Transport Budget"
                  name="transportBudget"
                  value={place.transportBudget}
                  onChange={(e) => handleInputChange(e, cityIndex, placeIndex)}
                />
                <input
                  type="number"
                  placeholder="Food Budget"
                  name="foodBudget"
                  value={place.foodBudget}
                  onChange={(e) => handleInputChange(e, cityIndex, placeIndex)}
                />
                <input
                  type="number"
                  placeholder="Activities Budget"
                  name="activitiesBudget"
                  value={place.activitiesBudget}
                  onChange={(e) => handleInputChange(e, cityIndex, placeIndex)}
                />
              </div>
            ))}
          </div>
        ))}
        <button onClick={handleAddCity}>Add Another City</button>
        <button onClick={handleGeneratePlanner}>Generate Expense Planner</button>
        <button onClick={handleDownloadPDF}>Download as PDF</button>

        {/* Display the generated expense plan */}
        {expensePlan.length > 0 && (
          <div>
            <h3>Generated Expense Plan</h3>
            {expensePlan.map((cityObj, cityIndex) => (
              <div key={cityIndex}>
                <h4>{cityObj.city} - Total Budget: ₹{cityObj.totalBudget}</h4>
                {cityObj.places.map((place, placeIndex) => (
                  <div key={placeIndex}>
                    <p>{place.name} - Total Budget for Place: ₹{place.totalPlaceBudget}</p>
                    <p>Transport: ₹{place.transportBudget}</p>
                    <p>Food: ₹{place.foodBudget}</p>
                    <p>Activities: ₹{place.activitiesBudget}</p>
                  </div>
                ))}
              </div>
            ))}
          </div>
        )}
      </div>

      <Link to="/expense-tracker" className="btn btn-primary m-2">Go to Expense Tracker</Link>
      <Link to="/budget" className="btn btn-secondary m-2">Go to Budget Management</Link>
    </div>
  );
};

export default Home;
