import React, { useState } from 'react';

const Budget = ({ expenses }) => {
  const [budget, setBudget] = useState('');
  const [totalExpenses, setTotalExpenses] = useState(0);

  // Calculate total expenses
  const calculateTotalExpenses = () => {
    const total = expenses.reduce((acc, expense) => acc + expense.amount, 0);
    setTotalExpenses(total);
  };

  const remainingBudget = budget - totalExpenses;

  const handleBudgetSubmit = (e) => {
    e.preventDefault();
    calculateTotalExpenses(); // Calculate total expenses when setting budget
  };

  return (
    <div>
      <h2 className="text-center">Budget Management</h2>
      <form className="mb-3" onSubmit={handleBudgetSubmit}>
        <div className="mb-3">
          <label htmlFor="budget" className="form-label">Set Your Budget:</label>
          <input
            type="number"
            id="budget"
            className="form-control"
            value={budget}
            onChange={(e) => setBudget(Number(e.target.value))}
            required
          />
        </div>
        <button type="submit" className="btn btn-primary">Set Budget</button>
      </form>

      {budget && (
        <div>
          <h4>Total Expenses: ${totalExpenses.toFixed(2)}</h4>
          <h4>Remaining Budget: ${remainingBudget.toFixed(2)}</h4>
        </div>
      )}
    </div>
  );
};

export default Budget;
