import React, { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import { Pie } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Filler,
  Tooltip,
  Legend,
  ArcElement,
} from 'chart.js';

// Register necessary chart components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Filler,
  Tooltip,
  Legend,
  ArcElement
);

const ExpenseTracker = ({ userId }) => {
  const [date, setDate] = useState('');
  const [expenses, setExpenses] = useState({});
  const [totalExpenses, setTotalExpenses] = useState(0);
  const [category, setCategory] = useState('Food');
  const [customCategory, setCustomCategory] = useState('');
  const [editingExpense, setEditingExpense] = useState(null);
  const [editingDate, setEditingDate] = useState(null);
  const [amount, setAmount] = useState(0);
  const [dates, setDates] = useState([new Date().toISOString().split("T")[0]]); // Initialize with today's date

  useEffect(() => {
    const fetchExpenses = async () => {
      try {
        const response = await fetch(`/api/expenses/${userId}`);
        if (!response.ok) {
          throw new Error('Failed to fetch expenses');
        }
        const data = await response.json();
        setExpenses(data.expenses);
        setTotalExpenses(data.totalExpenses);
      } catch (error) {
        console.error('Error fetching expenses:', error);
      }
    };

    fetchExpenses();
  }, [userId]);

  const handleDateChange = (event) => {
    setDate(event.target.value);
  };

  const handleAddDate = () => {
    const nextDate = new Date(dates[dates.length - 1]);
    nextDate.setDate(nextDate.getDate() + 1); // Add one day
    const formattedDate = nextDate.toISOString().split("T")[0]; // Format as YYYY-MM-DD
    setDates([...dates, formattedDate]);
    setDate(formattedDate); // Set new date to the selected date
  };

  const handleAddExpense = async (event) => {
    event.preventDefault();
    const amountValue = parseFloat(amount);
    const categoryToUse = category === 'Other' ? customCategory : category;

    if (date && !isNaN(amountValue) && categoryToUse) {
      const newExpenses = { ...expenses };
      if (!newExpenses[date]) {
        newExpenses[date] = [];
      }

      if (editingExpense) {
        const previousAmount = newExpenses[editingExpense.date][editingExpense.index].amount;
        newExpenses[editingExpense.date][editingExpense.index] = {
          amount: amountValue,
          category: categoryToUse,
        };
        setEditingExpense(null);
        setTotalExpenses(prevTotal => prevTotal - previousAmount + amountValue);
      } else {
        newExpenses[date].push({ amount: amountValue, category: categoryToUse });
        setTotalExpenses(prevTotal => prevTotal + amountValue);
      }

      setExpenses(newExpenses);

      // Save to backend (create or update)
      const savedExpense = await saveExpenseToBackend({ date, amount: amountValue, category: categoryToUse });
      if (savedExpense) {
        resetForm();
      }
    } else {
      console.error('Missing required fields');
    }
  };

  const saveExpenseToBackend = async (expense) => {
    try {
      const response = await fetch(`/api/expenses/${userId}`, {
        method: editingExpense ? 'PUT' : 'POST',  // Use POST for create, PUT for update
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(expense),
      });

      if (!response.ok) {
        throw new Error('Failed to save expense');
      }

      return await response.json(); // Optionally return the saved expense data
    } catch (error) {
      console.error('Error saving expense:', error);
    }
  };

  const handleEditExpense = (date, index) => {
    const expenseToEdit = expenses[date][index];
    setEditingExpense({ date, index });
    setAmount(expenseToEdit.amount);
    setCategory(expenseToEdit.category);
    if (expenseToEdit.category === 'Other') {
      setCustomCategory(expenseToEdit.category);
    } else {
      setCustomCategory('');
    }
  };

  const handleDeleteExpense = async (date, index) => {
    const expenseToDelete = expenses[date][index];
    const updatedExpenses = { ...expenses };
    updatedExpenses[date].splice(index, 1);  // Remove the selected expense

    // Update the total expenses
    setTotalExpenses(prevTotal => prevTotal - expenseToDelete.amount);
    setExpenses(updatedExpenses);

    // Delete from backend
    try {
      const response = await fetch(`/api/expenses/${userId}`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ date, amount: expenseToDelete.amount, category: expenseToDelete.category }),
      });

      if (!response.ok) {
        throw new Error('Failed to delete expense');
      }
    } catch (error) {
      console.error('Error deleting expense:', error);
    }
  };

  const handleEditDate = (oldDate) => {
    setEditingDate(oldDate);
    setDate(oldDate); // Populate the current date in the date input for editing
  };

  const handleDeleteDate = async (dateToDelete) => {
    const updatedExpenses = { ...expenses };
    const dateExpenses = updatedExpenses[dateToDelete];

    if (!dateExpenses) return; // If no expenses, do nothing

    delete updatedExpenses[dateToDelete];  // Remove the date and its expenses

    setExpenses(updatedExpenses);

    // Update total expenses
    const totalRemoved = dateExpenses.reduce((sum, expense) => sum + expense.amount, 0);
    setTotalExpenses(prevTotal => prevTotal - totalRemoved);

    // Delete from backend
    try {
      const response = await fetch(`/api/expenses/${userId}/date`, {
        method: 'DELETE', // Use DELETE to remove date and its expenses
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ date: dateToDelete }),
      });

      if (!response.ok) {
        throw new Error('Failed to delete date');
      }
    } catch (error) {
      console.error('Error deleting date:', error);
    }
  };

  const resetForm = () => {
    setAmount(0);
    setCategory('Food');
    setCustomCategory('');
    setEditingExpense(null);
    setEditingDate(null);
    setDate('');
  };

  const dailyTotals = Object.entries(expenses).map(([date, expenseList]) => {
    const dailyTotal = expenseList.reduce((sum, expense) => sum + expense.amount, 0);
    return { date, total: dailyTotal, expenses: expenseList };
  });

  const lineChartData = {
    labels: dailyTotals.map(entry => entry.date),
    datasets: [
      {
        label: 'Daily Spending',
        data: dailyTotals.map(entry => entry.total),
        fill: true,
        borderColor: 'rgba(75, 192, 192, 1)',
        backgroundColor: 'rgba(75, 192, 192, 0.2)',
        tension: 0.4,
      },
    ],
  };

  const categoryTotals = {};
  dailyTotals.forEach(entry => {
    entry.expenses.forEach(expense => {
      if (!categoryTotals[expense.category]) {
        categoryTotals[expense.category] = 0;
      }
      categoryTotals[expense.category] += expense.amount;
    });
  });

  const pieChartData = {
    labels: Object.keys(categoryTotals),
    datasets: [
      {
        data: Object.values(categoryTotals),
        backgroundColor: [
          'rgba(255, 99, 132, 0.6)',
          'rgba(54, 162, 235, 0.6)',
          'rgba(255, 206, 86, 0.6)',
          'rgba(75, 192, 192, 0.6)',
          'rgba(153, 102, 255, 0.6)',
          'rgba(255, 159, 64, 0.6)',
        ],
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: {
        display: true,
        position: 'top',
      },
    },
  };

  return (
    <div className="container mt-5">
      <h2 className="mb-4">Expense Tracker</h2>
      <div className="mb-3">
        <label htmlFor="date" className="form-label">Date:</label>
        <input
          type="date"
          id="date"
          className="form-control"
          value={date}
          onChange={handleDateChange}
        />
      </div>
      <button className="btn btn-secondary mb-3" onClick={handleAddDate}>
        Add Date
      </button>

      <form onSubmit={handleAddExpense}>
        <div className="mb-3">
          <label htmlFor="amount" className="form-label">Amount:</label>
          <input
            type="number"
            id="amount"
            className="form-control"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            required
          />
        </div>

        <div className="mb-3">
          <label htmlFor="category" className="form-label">Category:</label>
          <select
            id="category"
            className="form-control"
            value={category}
            onChange={(e) => {
              const selectedCategory = e.target.value;
              setCategory(selectedCategory);
              setCustomCategory(selectedCategory === 'Other' ? '' : '');
            }}
          >
            <option value="Food">Food</option>
            <option value="Transport">Transport</option>
            <option value="Accommodation">Accommodation</option>
            <option value="Other">Other</option>
          </select>
        </div>

        {category === 'Other' && (
          <div className="mb-3">
            <input
              type="text"
              placeholder="Enter Custom Category"
              className="form-control"
              value={customCategory}
              onChange={(e) => setCustomCategory(e.target.value)}
            />
          </div>
        )}

        <button type="submit" className="btn btn-primary">
          {editingExpense ? 'Update Expense' : 'Add Expense'}
        </button>
      </form>

      {dailyTotals.length > 0 && (
        <div className="mt-4">
          <h4>Daily Expense Summary</h4>
          <ul className="list-group">
            {dailyTotals.map(({ date, total, expenses }) => (
              <li key={date} className="list-group-item">
                <strong>{date}</strong>: ₹{total.toFixed(2)}
                <button
                  className="btn btn-link ms-2"
                  onClick={() => handleEditDate(date)}
                >
                  Edit Date
                </button>
                <button
                  className="btn btn-link text-danger ms-2"
                  onClick={() => handleDeleteDate(date)}
                >
                  Delete Date
                </button>
                <ul>
                  {expenses.map((expense, expIndex) => (
                    <li key={expIndex}>
                      {expense.category}: ₹{expense.amount.toFixed(2)}
                      <button
                        className="btn btn-link ms-2"
                        onClick={() => handleEditExpense(date, expIndex)}
                      >
                        Edit
                      </button>
                      <button
                        className="btn btn-link text-danger ms-2"
                        onClick={() => handleDeleteExpense(date, expIndex)}
                      >
                        Delete
                      </button>
                    </li>
                  ))}
                </ul>
              </li>
            ))}
          </ul>
        </div>
      )}

      <div className="mt-4">
        <h4>Total Expenses: ₹{totalExpenses.toFixed(2)}</h4>
      </div>

      <div className="mt-4">
        <h4>Spending Trends</h4>
        <div style={{ height: '300px' }}>
          <Line data={lineChartData} options={chartOptions} />
        </div>
      </div>

      <div className="mt-4">
        <h4>Category Breakdown</h4>
        <div style={{ height: '300px' }}>
          <Pie data={pieChartData} options={chartOptions} />
        </div>
      </div>
    </div>
  );
};

export default ExpenseTracker;
