import React, { useEffect, useState, useCallback } from 'react';
import { Bar } from 'react-chartjs-2';
import { Pie } from 'react-chartjs-2';
import axios from 'axios';
import { v4 as uuidv4 } from 'uuid';

const ExpenseTracker = () => {
  const [expenses, setExpenses] = useState([]);
  const [date, setDate] = useState('');
  const [amount, setAmount] = useState('');
  const [category, setCategory] = useState('food');
  const [totalExpense, setTotalExpense] = useState(0);
  const [chartData, setChartData] = useState({});
  const [categoryData, setCategoryData] = useState({});
  const [tips, setTips] = useState([
    "Plan your meals ahead to save on food expenses.",
    "Use public transport instead of taxis to reduce travel costs.",
    "Keep track of your daily expenses to avoid overspending.",
    "Set a budget for each category and stick to it.",
    "Look for discounts and offers when booking accommodation."
  ]);

  const userId = localStorage.getItem('userId'); // Assuming you store userId after login

  const fetchExpenses = useCallback(async () => {
    try {
      const response = await axios.get(`/api/expenses/${userId}`); // Fetch expenses from the backend
      setExpenses(response.data);
    } catch (error) {
      console.error('Error fetching expenses:', error);
    }
  }, [userId]);

  const calculateTotalExpense = useCallback(() => {
    const total = expenses.reduce((sum, expense) => sum + expense.amount, 0);
    setTotalExpense(total);
  }, [expenses]);

  const updateChartData = useCallback(() => {
    const chartData = {
      labels: expenses.map(expense => expense.date),
      datasets: [{
        label: 'Expenses',
        data: expenses.map(expense => expense.amount),
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
      }]
    };
    setChartData(chartData);
  }, [expenses]);

  const updateCategoryData = useCallback(() => {
    const categories = {};
    expenses.forEach(expense => {
      categories[expense.category] = (categories[expense.category] || 0) + expense.amount;
    });
    setCategoryData({
      labels: Object.keys(categories),
      datasets: [{
        data: Object.values(categories),
        backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#FF9F40'],
      }]
    });
  }, [expenses]);

  useEffect(() => {
    fetchExpenses();
  }, [fetchExpenses]);

  useEffect(() => {
    calculateTotalExpense();
    updateChartData();
    updateCategoryData();
  }, [expenses, calculateTotalExpense, updateChartData, updateCategoryData]);

  const addExpense = async () => {
    if (amount && date) {
      const newExpense = { id: uuidv4(), date, amount: parseFloat(amount), category };
      setExpenses([...expenses, newExpense]);
      await saveExpenseToDB(newExpense);
      setAmount('');
    }
  };

  const saveExpenseToDB = async (expense) => {
    try {
      await axios.post(`/api/expenses`, { ...expense, userId });
    } catch (error) {
      console.error('Error saving expense:', error);
    }
  };

  return (
    <div className="expense-tracker">
      <h2>Expense Tracker</h2>
      <div className="input-section">
        <input 
          type="date" 
          value={date} 
          onChange={(e) => setDate(e.target.value)} 
        />
        <input 
          type="number" 
          placeholder="Amount" 
          value={amount} 
          onChange={(e) => setAmount(e.target.value)} 
        />
        <select value={category} onChange={(e) => setCategory(e.target.value)}>
          <option value="food">Food</option>
          <option value="accommodation">Accommodation</option>
          <option value="travel">Travel</option>
          <option value="other">Other</option>
        </select>
        {category === 'other' && (
          <input 
            type="text" 
            placeholder="Specific Category" 
            onChange={(e) => setCategory(e.target.value)} 
          />
        )}
        <button onClick={addExpense}>Add Expense</button>
      </div>

      <h3>Total Expense: ${totalExpense}</h3>

      <div className="charts">
        <div className="bar-chart">
          <h4>Spending Trend Analysis</h4>
          <Bar data={chartData} width={100} height={50} />
        </div>
        <div className="pie-chart">
          <h4>Category Wise Breakdown</h4>
          <Pie data={categoryData} width={100} height={50} />
        </div>
      </div>

      <div className="tips">
        <h4>Expense Tips</h4>
        <ul>
          {tips.map((tip, index) => (
            <li key={index}>{tip}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default ExpenseTracker;
