import React from 'react';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    const email = e.target[0].value;
    const password = e.target[1].value;

    try {
      const res = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });

      const data = await res.json();

      if (res.ok) {
        localStorage.setItem('token', data.token);
        navigate('/home'); // Redirect to home page
      } else {
        alert(data.msg); // Display error message
      }
    } catch (error) {
      console.error('Error during login:', error);
      alert('An error occurred while logging in. Please try again.'); // General error message
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen">
      <h2 className="text-2xl font-bold mb-4">Login</h2>
      <form className="flex flex-col items-center w-full max-w-xs" onSubmit={handleLogin}>
        <input 
          className="w-full px-3 py-2 mb-3 text-base border rounded" 
          type="email" 
          placeholder="Email" 
          required 
        />
        <input 
          className="w-full px-3 py-2 mb-4 text-base border rounded" 
          type="password" 
          placeholder="Password" 
          required 
        />
        <button 
          className="w-full px-4 py-2 text-white bg-blue-500 rounded hover:bg-blue-600" 
          type="submit"
        >
          Login
        </button>
      </form>
    </div>
  );
};

export default Login;
