import React, { useState } from 'react';

const ExpenseTracker = () => {
  const [expenses, setExpenses] = useState([]);
  const [expenseInput, setExpenseInput] = useState({
    description: '',
    amount: '',
    category: '',
    date: '',
  });

  const handleChange = (e) => {
    setExpenseInput({
      ...expenseInput,
      [e.target.name]: e.target.value,
    });
  };

  const handleAddExpense = () => {
    const newExpense = {
      ...expenseInput,
      id: Date.now(),
    };
    setExpenses([...expenses, newExpense]);

    // Clear input fields after adding an expense
    setExpenseInput({
      description: '',
      amount: '',
      category: '',
      date: '',
    });
  };

  const handleDeleteExpense = (id) => {
    const updatedExpenses = expenses.filter((expense) => expense.id !== id);
    setExpenses(updatedExpenses);
  };

  return (
    <div className="container-fluid p-4">
      <h1 className="text-center mb-4">Expense Tracker</h1>
      <div className="row justify-content-center">
        <div className="col-md-8">
          <div className="card shadow-sm">
            <div className="card-body">
              <h4>Add New Expense</h4>
              <div className="mb-3">
                <input
                  type="text"
                  name="description"
                  value={expenseInput.description}
                  onChange={handleChange}
                  placeholder="Expense Description"
                  className="form-control"
                />
              </div>
              <div className="mb-3">
                <input
                  type="number"
                  name="amount"
                  value={expenseInput.amount}
                  onChange={handleChange}
                  placeholder="Amount"
                  className="form-control"
                />
              </div>
              <div className="mb-3">
                <input
                  type="text"
                  name="category"
                  value={expenseInput.category}
                  onChange={handleChange}
                  placeholder="Category (e.g., Food, Transport)"
                  className="form-control"
                />
              </div>
              <div className="mb-3">
                <input
                  type="date"
                  name="date"
                  value={expenseInput.date}
                  onChange={handleChange}
                  className="form-control"
                />
              </div>
              <button className="btn btn-primary" onClick={handleAddExpense}>
                Add Expense
              </button>
            </div>
          </div>

          <div className="mt-5">
            <h4>Expense List</h4>
            {expenses.length > 0 ? (
              <ul className="list-group">
                {expenses.map((expense) => (
                  <li key={expense.id} className="list-group-item d-flex justify-content-between align-items-center">
                    <span>
                      {expense.description} - ₹{expense.amount} ({expense.category}) on {expense.date}
                    </span>
                    <button className="btn btn-danger btn-sm" onClick={() => handleDeleteExpense(expense.id)}>
                      Delete
                    </button>
                  </li>
                ))}
              </ul>
            ) : (
              <p>No expenses added yet.</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ExpenseTracker;
