import React, { useEffect, useRef } from 'react';
import Chart from 'chart.js/auto';

const BarChartComponent = () => {
  const chartRef = useRef(null);
  let chartInstance = null;

  useEffect(() => {
    if (chartInstance) {
      chartInstance.destroy();
    }

    const ctx = chartRef.current.getContext("2d");

    // Create a gradient fill style with animation
    const gradient = ctx.createLinearGradient(0, 0, 0, 400);
    gradient.addColorStop(0, "rgba(75,192,192,0.2)");
    gradient.addColorStop(0.5, "rgba(75,192,192,0.4)");
    gradient.addColorStop(1, "rgba(75,192,192,0.2)");

    chartInstance = new Chart(ctx, {
      type: "bar",
      data: {
        labels: ["Label 1", "Label 2", "Label 3", "Label 4"],
        datasets: [
          {
            label: "Dataset 1",
            data: [12, 19, 3, 5],
            backgroundColor: gradient, // Apply the gradient as the fill
            borderColor: "rgba(75,192,192,1)",
            borderWidth: 1,
          }
        ]
      },
      options: {
        responsive: true,
        plugins: {
          legend: {
            display: false
          }
        },
        scales: {
          y: {
            beginAtZero: true
          }
        }
      }
    });
  }, []);

  return (
    <>
      <canvas ref={chartRef} style={{ width: '100%', height: '400px' }} />
      <style>
        {`
          /* Add CSS for animating gradient */
          @keyframes gradientMove {
            0% { background-position: 0% 50%; }
            50% { background-position: 100% 50%; }
            100% { background-position: 0% 50%; }
          }
          canvas {
            background: linear-gradient(45deg, rgba(75,192,192,0.2), rgba(75,192,192,0.4));
            background-size: 200% 200%;
            animation: gradientMove 5s ease infinite;
          }
        `}
      </style>
    </>
  );
};

export default BarChartComponent;
