import React, { useState } from 'react';

const ExpenseTracker = () => {
  const [date, setDate] = useState('');
  const [expenses, setExpenses] = useState({});
  const [totalExpenses, setTotalExpenses] = useState(0);
  const [category, setCategory] = useState('Food'); // Default category
  const [customCategory, setCustomCategory] = useState('');
  const [editingExpense, setEditingExpense] = useState(null);
  const [editingDate, setEditingDate] = useState('');

  const handleDateChange = (event) => {
    setDate(event.target.value);
  };

  const handleAddExpense = (event) => {
    event.preventDefault();
    const form = event.target;
    const amount = parseFloat(form.amount.value);
    const categoryToUse = category === 'Other' ? customCategory : category;

    if (date && amount && categoryToUse) {
      const newExpenses = { ...expenses };
      if (!newExpenses[date]) {
        newExpenses[date] = [];
      }

      // If editing, update the existing expense
      if (editingExpense) {
        const updatedExpenses = newExpenses[editingDate].map((expense) =>
          expense.id === editingExpense.id
            ? { ...expense, amount, category: categoryToUse }
            : expense
        );
        newExpenses[editingDate] = updatedExpenses;

        // Reset editing state
        setEditingExpense(null);
        setEditingDate('');
      } else {
        // Add new expense
        const expenseId = Math.random().toString(36).substr(2, 9); // Unique ID for expense
        newExpenses[date].push({ id: expenseId, amount, category: categoryToUse });
      }

      setExpenses(newExpenses);
      setTotalExpenses((prevTotal) => prevTotal + (editingExpense ? 0 : amount)); // Adjust total
      form.reset(); // Reset form fields
      setCategory('Food'); // Reset category to default
      setCustomCategory(''); // Reset custom category
      setDate(''); // Reset date
    }
  };

  const handleNextDate = () => {
    setDate('');
    setExpenses({ ...expenses }); // Keep previous expenses for the next date
  };

  // Calculate daily totals
  const dailyTotals = Object.entries(expenses).map(([date, expenseList]) => {
    const dailyTotal = expenseList.reduce((sum, expense) => sum + expense.amount, 0);
    return { date, total: dailyTotal, expenses: expenseList };
  });

  const handleEditExpense = (expense) => {
    setEditingExpense(expense);
    setEditingDate(date);
    setCategory(expense.category);
    setCustomCategory(expense.category === 'Other' ? expense.customCategory : '');
  };

  return (
    <div className="container">
      <h2 className="text-center mb-4">Expense Tracker</h2>

      <div className="mb-4">
        <input
          type="date"
          value={date}
          onChange={handleDateChange}
          className="form-control"
        />
      </div>

      {date && (
        <form onSubmit={handleAddExpense}>
          <div className="mb-3">
            <input
              type="number"
              name="amount"
              placeholder="Expense Amount"
              required
              className="form-control"
            />
          </div>
          <div className="mb-3">
            <select
              className="form-select"
              value={category}
              onChange={(e) => {
                setCategory(e.target.value);
                if (e.target.value !== 'Other') {
                  setCustomCategory(''); // Clear custom category if not 'Other'
                }
              }}
            >
              <option value="Food">Food</option>
              <option value="Accommodation">Accommodation</option>
              <option value="Travel">Travel</option>
              <option value="Other">Other</option>
            </select>
          </div>
          {category === 'Other' && (
            <div className="mb-3">
              <input
                type="text"
                value={customCategory}
                onChange={(e) => setCustomCategory(e.target.value)}
                placeholder="Specify Other Category"
                className="form-control"
                required
              />
            </div>
          )}
          <button type="submit" className="btn btn-primary">
            {editingExpense ? 'Update Expense' : 'Add Expense'}
          </button>
        </form>
      )}

      {dailyTotals.length > 0 && (
        <div className="mt-4">
          <h4 className="text-center">Daily Expense Summary</h4>
          {dailyTotals.map(({ date, total, expenses }, index) => (
            <div key={index}>
              <h5>{date}: Total Expenses = ₹{total.toFixed(2)}</h5>
              <ul className="list-group mb-3">
                {expenses.map((expense) => (
                  <li key={expense.id} className="list-group-item d-flex justify-content-between align-items-center">
                    {expense.category}: ₹{expense.amount.toFixed(2)}
                    <button className="btn btn-warning btn-sm" onClick={() => handleEditExpense(expense)}>
                      Edit
                    </button>
                  </li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      )}

      <div className="mt-4 text-center">
        <h5>Total Expenses: ₹{totalExpenses.toFixed(2)}</h5>
        <button className="btn btn-secondary" onClick={handleNextDate}>
          Next Date
        </button>
      </div>
    </div>
  );
};

export default ExpenseTracker;
