import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
  const [totalBudget, setTotalBudget] = useState(0);
  const [cities, setCities] = useState([]);
  const [cityName, setCityName] = useState('');
  const [places, setPlaces] = useState('');
  const [planner, setPlanner] = useState([]);

  const handleAddCity = () => {
    setCities([...cities, { name: cityName, places: places.split(',') }]);
    setCityName('');
    setPlaces('');
  };

  const calculateExpenses = () => {
    const calculatedPlanner = cities.map((city) => {
      const budgetPerPlace = totalBudget / city.places.length;
      const transport = budgetPerPlace * 0.3;
      const food = budgetPerPlace * 0.25;
      const accommodation = budgetPerPlace * 0.2;
      const activities = budgetPerPlace * 0.15;
      const misc = budgetPerPlace * 0.1;

      return {
        city: city.name,
        places: city.places.map((place) => ({
          name: place,
          transport: transport.toFixed(2),
          food: food.toFixed(2),
          accommodation: accommodation.toFixed(2),
          activities: activities.toFixed(2),
          misc: misc.toFixed(2),
          total: budgetPerPlace.toFixed(2),
        })),
      };
    });

    setPlanner(calculatedPlanner);
  };

  return (
    <div className="text-center">
      <h1>Welcome to Travel Toes</h1>
      <p>Your ultimate expense tracker and budget management tool.</p>

      {/* Total Budget Input */}
      <input
        type="number"
        placeholder="Enter Total Budget"
        value={totalBudget}
        onChange={(e) => setTotalBudget(Number(e.target.value))}
        className="form-control my-3"
      />

      {/* City and Places Input */}
      <input
        type="text"
        placeholder="Enter City Name"
        value={cityName}
        onChange={(e) => setCityName(e.target.value)}
        className="form-control my-2"
      />
      <input
        type="text"
        placeholder="Enter Places (comma-separated)"
        value={places}
        onChange={(e) => setPlaces(e.target.value)}
        className="form-control my-2"
      />
      <button className="btn btn-primary m-2" onClick={handleAddCity}>
        Add City
      </button>

      {/* Generate Expense Planner */}
      <button className="btn btn-success m-2" onClick={calculateExpenses}>
        Generate Expense Planner
      </button>

      {/* Display Planner */}
      {planner.length > 0 && (
        <div>
          <h2>Temporary Expense Planner</h2>
          {planner.map((city, index) => (
            <div key={index}>
              <h3>{city.city}</h3>
              {city.places.map((place, i) => (
                <div key={i} className="expense-item">
                  <h4>{place.name}</h4>
                  <p>Transport: ${place.transport}</p>
                  <p>Food: ${place.food}</p>
                  <p>Accommodation: ${place.accommodation}</p>
                  <p>Activities: ${place.activities}</p>
                  <p>Misc: ${place.misc}</p>
                  <p>Total: ${place.total}</p>
                </div>
              ))}
            </div>
          ))}
        </div>
      )}

      <Link to="/expense-tracker" className="btn btn-primary m-2">
        Go to Expense Tracker
      </Link>
      <Link to="/budget" className="btn btn-secondary m-2">
        Go to Budget Management
      </Link>
    </div>
  );
};

export default Home;
