// Login.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const res = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });

      const data = await res.json();

      if (res.ok) {
        localStorage.setItem('token', data.token);
        navigate('/home'); // Redirect to home page
      } else {
        setErrorMessage(data.msg); // Set error message to display
      }
    } catch (error) {
      console.error('Login error:', error);
      setErrorMessage('An error occurred while logging in. Please try again.'); // Display generic error message
    }
  };

  return (
    <div>
      <h2 className="text-center">Login</h2>
      {errorMessage && <div className="alert alert-danger">{errorMessage}</div>} {/* Display error message */}
      <form className="mb-3" onSubmit={handleLogin}>
        <input
          className="form-control"
          type="email"
          placeholder="Email"
          required
          value={email}
          onChange={(e) => setEmail(e.target.value)} // Handle email input
        />
        <input
          className="form-control mt-2"
          type="password"
          placeholder="Password"
          required
          value={password}
          onChange={(e) => setPassword(e.target.value)} // Handle password input
        />
        <button className="btn btn-primary mt-3" type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
