import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

// Import only the icons we need from @/components/ui/icons
import { 
  CircleIcon, 
  DollarSignIcon, 
  TrendingUpIcon, 
  AlertCircleIcon, 
  PieChartIcon, 
  CalendarIcon, 
  SettingsIcon 
} from '@/components/ui/icons';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

// Animated Progress Ring Component
const AnimatedProgressRing = ({ percentage, color }) => {
  const [progress, setProgress] = useState(0);

  useEffect(() => {
    setTimeout(() => setProgress(percentage), 100);
  }, [percentage]);

  const radius = 40;
  const circumference = 2 * Math.PI * radius;
  const strokeDashoffset = circumference - (progress / 100) * circumference;

  return (
    <div className="relative inline-flex items-center justify-center">
      <svg className="transform -rotate-90 w-24 h-24">
        <circle
          className="text-gray-200"
          strokeWidth="8"
          stroke="currentColor"
          fill="transparent"
          r={radius}
          cx="48"
          cy="48"
        />
        <circle
          className={`${color}`}
          strokeWidth="8"
          strokeDasharray={circumference}
          strokeDashoffset={strokeDashoffset}
          strokeLinecap="round"
          stroke="currentColor"
          fill="transparent"
          r={radius}
          cx="48"
          cy="48"
          style={{ transition: 'stroke-dashoffset 0.5s ease-in-out' }}
        />
      </svg>
      <span className="absolute text-xl font-bold">{Math.round(progress)}%</span>
    </div>
  );
};

// Floating Card Component
const FloatingCard = ({ children, delay = 0 }) => {
  return (
    <div 
      className="transform hover:scale-105 transition-all duration-300 ease-in-out"
      style={{
        animation: `float 3s ease-in-out infinite ${delay}s`,
      }}
    >
      {children}
    </div>
  );
};

// Budget Tips Component
const BudgetTips = () => {
  const tips = [
    {
      icon: <DollarSignIcon className="w-8 h-8" />,
      text: "Use the 50/30/20 rule: 50% for needs, 30% for wants, and 20% for savings.",
      color: "bg-blue-100"
    },
    {
      icon: <TrendingUpIcon className="w-8 h-8" />,
      text: "Set SMART financial goals: Specific, Measurable, Achievable, Relevant, and Time-bound.",
      color: "bg-green-100"
    },
    {
      icon: <PieChartIcon className="w-8 h-8" />,
      text: "Diversify your savings and investment portfolio.",
      color: "bg-purple-100"
    },
    {
      icon: <CalendarIcon className="w-8 h-8" />,
      text: "Review and adjust your budget regularly - weekly check-ins help catch overspending early.",
      color: "bg-yellow-100"
    }
  ];

  return (
    <div className="mt-8 mb-8">
      <h2 className="text-2xl font-bold text-center mb-6">
        Smart Budgeting Tips
      </h2>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
        {tips.map((tip, index) => (
          <FloatingCard key={index} delay={index * 0.2}>
            <div className={`rounded-lg p-6 h-full ${tip.color} shadow-lg transform transition-all duration-300 hover:scale-105`}>
              <div className="flex flex-col items-center text-center gap-4">
                <div className="text-primary">{tip.icon}</div>
                <p className="text-gray-700">{tip.text}</p>
              </div>
            </div>
          </FloatingCard>
        ))}
      </div>
    </div>
  );
};

// Budget Summary Card Component
const BudgetSummaryCard = ({ title, amount, total, color, icon: Icon }) => {
  const percentage = total > 0 ? (amount / total) * 100 : 0;

  return (
    <FloatingCard>
      <div className="bg-white rounded-lg shadow-lg p-6">
        <div className="flex items-center gap-3 mb-4">
          <Icon className="w-6 h-6" />
          <h3 className="text-lg font-semibold">{title}</h3>
        </div>
        <div className="flex items-center justify-between">
          <AnimatedProgressRing percentage={percentage} color={color} />
          <div className="text-right">
            <p className="text-2xl font-bold">${amount.toFixed(2)}</p>
            <p className="text-gray-500">of ${total.toFixed(2)}</p>
          </div>
        </div>
      </div>
    </FloatingCard>
  );
};

// Loading Spinner Component
const LoadingSpinner = () => (
  <div className="flex justify-center items-center h-screen">
    <div className="animate-spin rounded-full h-32 w-32 border-b-4 border-primary"></div>
  </div>
);

// Main Budget Management Component
const BudgetManagement = () => {
  const [budgets, setBudgets] = useState([]);
  const [category, setCategory] = useState('Food');
  const [customCategory, setCustomCategory] = useState('');
  const [budgetAmount, setBudgetAmount] = useState('');
  const [expenses, setExpenses] = useState([]);
  const [totalBudget, setTotalBudget] = useState(0);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState('');
  const [showCustomCategory, setShowCustomCategory] = useState(false);

  useEffect(() => {
    const initializeData = async () => {
      try {
        await Promise.all([fetchExpenses(), fetchBudgets()]);
      } catch (error) {
        console.error('Error initializing data:', error);
      } finally {
        setIsLoading(false);
      }
    };

    initializeData();
  }, []);

  useEffect(() => {
    setShowCustomCategory(category === 'Other');
    if (category !== 'Other') {
      setCustomCategory('');
    }
  }, [category]);

  const fetchBudgets = async () => {
    setError('');
    try {
      const token = localStorage.getItem('token');
      if (!token) throw new Error('No authentication token found');

      const response = await fetch('http://localhost:5000/api/budgets', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to fetch budgets');
      }

      const data = await response.json();
      const transformedData = Array.isArray(data) ? data : (data.budgets || []);
      const processedBudgets = transformedData.map(budget => ({
        ...budget,
        category: budget.category || 'Unknown',
        amount: Number(budget.amount) || 0
      }));

      setBudgets(processedBudgets);
      calculateTotalBudget(processedBudgets);
    } catch (error) {
      setError(`Error fetching budgets: ${error.message}`);
      setBudgets([]);
    }
  };

  const fetchExpenses = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) throw new Error('No authentication token found');

      const response = await fetch('http://localhost:5000/api/expenses', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to fetch expenses');
      }

      const data = await response.json();
      setExpenses(Array.isArray(data.expenses) ? data.expenses : []);
    } catch (error) {
      setError(`Error fetching expenses: ${error.message}`);
      setExpenses([]);
    }
  };

  const calculateTotalBudget = (budgetsList) => {
    const total = (budgetsList || []).reduce((sum, budget) => {
      return sum + (Number(budget.amount) || 0);
    }, 0);
    setTotalBudget(total);
  };

  const handleSetBudget = async (e) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);

    try {
      const amount = parseFloat(budgetAmount);
      if (isNaN(amount) || amount <= 0) {
        throw new Error('Please enter a valid budget amount greater than 0');
      }

      const token = localStorage.getItem('token');
      if (!token) throw new Error('No authentication token found');

      const actualCategory = category === 'Other' ? customCategory : category;
      const existingBudget = budgets.find(b => b.category === actualCategory);

      const method = existingBudget ? 'PUT' : 'POST';
      const url = existingBudget 
        ? `http://localhost:5000/api/budgets/${existingBudget._id}`
        : 'http://localhost:5000/api/budgets';

      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          category: actualCategory,
          amount
        })
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to set budget');
      }

      await fetchBudgets();
      setBudgetAmount('');
      if (category === 'Other') {
        setCustomCategory('');
      }
    } catch (error) {
      setError(`Error setting budget: ${error.message}`);
    } finally {
      setIsLoading(false);
    }
  };

  const handleDeleteBudget = async (categoryToDelete) => {
    setIsLoading(true);
    setError('');

    try {
      const token = localStorage.getItem('token');
      if (!token) throw new Error('No authentication token found');

      const budgetToDelete = budgets.find(b => b.category === categoryToDelete);
      if (!budgetToDelete) throw new Error('Budget not found');

      const response = await fetch(`http://localhost:5000/api/budgets/${budgetToDelete._id}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to delete budget');
      }

      await fetchBudgets();
    } catch (error) {
      setError(`Error deleting budget: ${error.message}`);
    } finally {
      setIsLoading(false);
    }
  };

  const calculateAmountSpent = (budgetCategory) => {
    return expenses
      .filter(expense => expense.category === budgetCategory)
      .reduce((sum, expense) => sum + (parseFloat(expense.amount) || 0), 0);
  };

  const calculateTotalSpent = () => {
    return expenses.reduce((sum, expense) => sum + (parseFloat(expense.amount) || 0), 0);
  };

  const calculateBalance = () => {
    return totalBudget - calculateTotalSpent();
  };

  const chartData = {
    labels: budgets.map(budget => budget?.category || 'Unknown'),
    datasets: [
      {
        label: 'Budget Amount',
        data: budgets.map(budget => Number(budget?.amount) || 0),
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
        borderColor: 'rgba(75, 192, 192, 1)',
        borderWidth: 1,
      },
      {
        label: 'Actual Spending',
        data: budgets.map(budget => calculateAmountSpent(budget?.category)),
        backgroundColor: 'rgba(255, 99, 132, 0.6)',
        borderColor: 'rgba(255, 99, 132, 1)',
        borderWidth: 1,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: true,
    aspectRatio: 2,
    animation: {
      duration: 2000,
      easing: 'easeInOutQuart',
    },
    plugins: {
      legend: {
        position: 'top',
        labels: {
          font: {
            size: 14
          }
        }
      },
      title: {
        display: true,
        text: 'Budget vs. Spending by Category',
        font: {
          size: 16,
          weight: 'bold'
        }
      },
    },
    scales: {
      y: {
        beginAtZero: true,
        grid: {
          color: 'rgba(0, 0, 0, 0.1)',
        }
      },
      x: {
        grid: {
          display: false
        }
      }
    }
  };

  if (isLoading) return <LoadingSpinner />;

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-4xl font-bold text-center mb-8 text-primary">
        Budget Dashboard
      </h1>

      {error && (
        <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 mb-6 rounded-r" role="alert">
          <p className="font-medium">{error}</p>
        </div>
      )}

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        <BudgetSummaryCard 
          title="Total Budget"
          amount={totalBudget}
          total={totalBudget}
          color="text-blue-500"
          icon={DollarSign}
        />
        <BudgetSummaryCard 
          title="Amount Spent"
          amount={calculateTotalSpent()}
          total={totalBudget}
          color="text-green-500"
          icon={PieChart}
        />
        <BudgetSummaryCard 
          title="Balance"
          amount={Math.max(0, calculateBalance())}
          total={totalBudget}
          color="text-purple-500"
          icon={Settings}
        />
      </div>

      <div className="space-y-8">
        <FloatingCard>
          <div className="bg-white rounded-lg shadow-lg p-6">
            <h2 className="text-2xl font-bold mb-6">Set Budget</h2>
            <form onSubmit={handleSetBudget} className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Category:
                </label>
                <select 
                  className="w-full rounded-md border border-gray-300 px-3 py-2 focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent"
                  value={category}
                  onChange={(e) => setCategory(e.target.value)}
                  disabled={isLoading}
                >
                  <option value="Food">Food</option>
                  <option value="Transport">Transport</option>
                  <option value="Shopping">Shopping</option>
                  <option value="Entertainment">Entertainment</option>
                  <option value="Other">Other</option>
                </select>
              </div>

              {showCustomCategory && (
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Custom Category Name:
                  </label>
                  <input
                    type="text"
                    className="w-full rounded-md border border-gray-300 px-3 py-2 focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent"
                    value={customCategory}
                    onChange={(e) => setCustomCategory(e.target.value)}
                    placeholder="Enter custom category name"
                    required
                    disabled={isLoading}
                  />
                </div>
              )}

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Budget Amount:
                </label>
                <input
                  type="number"
                  className="w-full rounded-md border border-gray-300 px-3 py-2 focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent"
                  value={budgetAmount}
                  onChange={(e) => setBudgetAmount(e.target.value)}
                  min="0"
                  step="0.01"
                  required
                  disabled={isLoading}
                />
              </div>

              <button 
                type="submit" 
                className="w-full bg-primary text-white py-2 px-4 rounded-md hover:bg-opacity-90 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
                disabled={isLoading}
              >
                {isLoading ? 'Setting Budget...' : 'Set Budget'}
              </button>
            </form>
          </div>
        </FloatingCard>

        <FloatingCard>
          <div className="bg-white rounded-lg shadow-lg p-6">
            <h2 className="text-2xl font-bold mb-6">Budget Overview</h2>
            <div className="overflow-x-auto">
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Category</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Budget Amount</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Amount Spent</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Remaining</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {budgets.length > 0 ? budgets.map((budget) => {
                    const amountSpent = calculateAmountSpent(budget.category);
                    const remaining = (budget.amount || 0) - amountSpent;
                    return (
                      <tr key={budget.category} className="hover:bg-gray-50">
                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{budget.category}</td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">${budget.amount ? budget.amount.toFixed(2) : '0.00'}</td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">${amountSpent.toFixed(2)}</td>
                        <td className={`px-6 py-4 whitespace-nowrap text-sm font-medium ${remaining < 0 ? 'text-red-600' : 'text-green-600'}`}>
                          ${remaining.toFixed(2)}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm">
                          <button
                            onClick={() => handleDeleteBudget(budget.category)}
                            disabled={isLoading}
                            className="text-red-600 hover:text-red-900 disabled:opacity-50 disabled:cursor-not-allowed focus:outline-none"
                          >
                            <AlertCircle className="w-5 h-5" />
                          </button>
                        </td>
                      </tr>
                    );
                  }) : (
                    <tr>
                      <td colSpan="5" className="px-6 py-4 whitespace-nowrap text-sm text-gray-500 text-center">
                        No budgets available
                      </td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          </div>
        </FloatingCard>

        <FloatingCard>
          <div className="bg-white rounded-lg shadow-lg p-6">
            <h2 className="text-2xl font-bold mb-6">Budget vs. Spending</h2>
            <div className="h-96">
              <Bar 
                data={chartData} 
                options={chartOptions}
                className="w-full h-full"
              />
            </div>
          </div>
        </FloatingCard>

        <BudgetTips />

        <FloatingCard>
          <div className="bg-white rounded-lg shadow-lg p-6 text-center">
            <h2 className="text-2xl font-bold mb-6">Explore More</h2>
            <p className="text-gray-600 mb-4">
              Track your expenses in detail with our expense tracker
            </p>
            <Link 
              to="/expense-tracker" 
              className="inline-flex items-center gap-2 bg-primary text-white py-2 px-6 rounded-md hover:bg-opacity-90 transition-colors"
            >
              <span>Go to Expense Tracker</span>
              <TrendingUp className="w-4 h-4" />
            </Link>
          </div>
        </FloatingCard>
      </div>
    </div>
  );
};

export default BudgetManagement;
