import React, { useState, useEffect } from 'react';
import { Bar } from 'react-chartjs-2';
import { useNavigate, Link } from 'react-router-dom';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const BudgetManagement = () => {
  const navigate = useNavigate();
  const [budgets, setBudgets] = useState([]);
  const [category, setCategory] = useState('Food');
  const [amount, setAmount] = useState('');
  const [customCategory, setCustomCategory] = useState('');
  const [editingBudget, setEditingBudget] = useState(null);
  const [totalExpenses, setTotalExpenses] = useState(0); // Changed from monthlyExpenses
  const [totalBudget, setTotalBudget] = useState(0);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/');
      return;
    }
    fetchBudgets();
    fetchTotalExpenses(); // Updated function call
  }, [navigate]);

  const fetchBudgets = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/budgets', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        setBudgets(data.budgets);
        setTotalBudget(data.totalBudget);
      } else if (response.status === 401) {
        localStorage.removeItem('token');
        navigate('/');
      }
    } catch (error) {
      console.error('Error fetching budgets:', error);
    }
  };

  const fetchTotalExpenses = async () => { // Updated function name
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/expenses/total', { // Updated API endpoint
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        setTotalExpenses(data.totalExpenses); // Updated state
      }
    } catch (error) {
      console.error('Error fetching total expenses:', error);
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const token = localStorage.getItem('token');

    if (!token) {
      navigate('/');
      return;
    }

    const amountValue = parseFloat(amount);
    const categoryValue = category === 'Other' ? customCategory : category;

    if (isNaN(amountValue) || !categoryValue) {
      alert('Please fill in all fields correctly');
      return;
    }

    const budgetData = {
      category: categoryValue,
      amount: amountValue,
    };

    try {
      let url = 'http://localhost:5000/api/budgets';
      let method = 'POST';

      if (editingBudget) {
        url = `http://localhost:5000/api/budgets/${editingBudget._id}`;
        method = 'PUT';
      }

      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(budgetData)
      });

      if (response.ok) {
        resetForm();
        fetchBudgets();
      } else {
        const error = await response.json();
        alert(error.message || 'Failed to save budget. Please try again.');
      }
    } catch (error) {
      console.error('Error saving budget:', error);
      alert('Failed to save budget. Please try again.');
    }
  };

  const handleDeleteBudget = async (budgetId) => {
    if (!window.confirm('Are you sure you want to delete this budget?')) {
      return;
    }

    const token = localStorage.getItem('token');
    try {
      const response = await fetch(`http://localhost:5000/api/budgets/${budgetId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        fetchBudgets();
      } else {
        const error = await response.json();
        alert(error.message || 'Failed to delete budget.');
      }
    } catch (error) {
      console.error('Error deleting budget:', error);
      alert('Failed to delete budget. Please try again.');
    }
  };

  const handleEditBudget = (budget) => {
    setEditingBudget(budget);
    setAmount(budget.amount.toString());

    if (['Food', 'Transport', 'Shopping', 'Entertainment'].includes(budget.category)) {
      setCategory(budget.category);
      setCustomCategory('');
    } else {
      setCategory('Other');
      setCustomCategory(budget.category);
    }
  };

  const resetForm = () => {
    setAmount('');
    setCategory('Food');
    setCustomCategory('');
    setEditingBudget(null);
  };

  // Chart data preparation
  const chartData = {
    labels: budgets.map(budget => budget.category),
    datasets: [
      {
        label: 'Budget Amount',
        data: budgets.map(budget => budget.amount),
        backgroundColor: 'rgba(53, 162, 235, 0.5)',
        borderColor: 'rgba(53, 162, 235, 1)',
        borderWidth: 1,
      },
      {
        label: 'Actual Spending',
        data: budgets.map(budget => totalExpenses || 0), // Updated to reflect totalExpenses
        backgroundColor: 'rgba(255, 99, 132, 0.5)',
        borderColor: 'rgba(255, 99, 132, 1)',
        borderWidth: 1,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: true,
    aspectRatio: 1.5,
    plugins: {
      legend: {
        position: 'top',
        labels: {
          font: {
            size: 10
          }
        }
      }
    },
    scales: {
      x: {
        ticks: {
          font: {
            size: 9
          }
        }
      },
      y: {
        beginAtZero: true,
        ticks: {
          font: {
            size: 9
          }
        }
      }
    }
  };

  const balanceAmount = totalBudget - totalExpenses; // Calculate balance amount

  return (
    <div className="container mt-5">
      <div className="text-center mb-4">
        <h2 className="display-4 mb-3">Budget Management</h2>
        <h3 className="text-muted">Total Trip Budget: ${totalBudget.toFixed(2)}</h3>
        <h4 className="text-muted">Balance Amount: ${balanceAmount.toFixed(2)}</h4> {/* Added balance amount */}
      </div>

      <div className="row">
        <div className="col-md-6">
          <div className="card shadow-sm mb-4">
            <div className="card-body">
              <h4 className="card-title mb-3">Set Budget</h4>
              <form onSubmit={handleSubmit}>
                <div className="mb-3">
                  <label htmlFor="category" className="form-label">Category:</label>
                  <select
                    id="category"
                    className="form-select"
                    value={category}
                    onChange={(e) => {
                      if (e.target.value === 'Other') {
                        setCustomCategory('');
                      }
                      setCategory(e.target.value);
                    }}
                  >
                    <option value="Food">Food</option>
                    <option value="Transport">Transport</option>
                    <option value="Shopping">Shopping</option>
                    <option value="Entertainment">Entertainment</option>
                    <option value="Other">Other</option>
                  </select>
                  {category === 'Other' && (
                    <input
                      type="text"
                      className="form-control mt-2"
                      placeholder="Specify category"
                      value={customCategory}
                      onChange={(e) => setCustomCategory(e.target.value)}
                      required
                    />
                  )}
                </div>

                <div className="mb-3">
                  <label htmlFor="amount" className="form-label">Budget Amount:</label>
                  <input
                    type="number"
                    id="amount"
                    className="form-control"
                    value={amount}
                    onChange={(e) => setAmount(e.target.value)}
                    required
                    step="0.01"
                    min="0"
                  />
                </div>

                <div className="d-grid">
                  <button type="submit" className="btn btn-primary">
                    {editingBudget ? 'Update Budget' : 'Set Budget'}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>

        <div className="col-md-6">
          <div className="card shadow-sm mb-4">
            <div className="card-body">
            <h4 className="card-title mb-3">Current Budgets</h4>
              <ul className="list-group">
                {budgets.map((budget) => (
                  <li key={budget._id} className="list-group-item d-flex justify-content-between align-items-center">
                    {budget.category}: ${budget.amount.toFixed(2)}
                    <div>
                      <button
                        className="btn btn-warning btn-sm me-2"
                        onClick={() => handleEditBudget(budget)}
                      >
                        Edit
                      </button>
                      <button
                        className="btn btn-danger btn-sm"
                        onClick={() => handleDeleteBudget(budget._id)}
                      >
                        Delete
                      </button>
                    </div>
                  </li>
                ))}
              </ul>
            </div>
          </div>
        </div>
      </div>

      <div className="mt-5">
        <h4 className="mb-3">Budget vs Actual Spending</h4>
        <Bar data={chartData} options={chartOptions} />
      </div>
    </div>
  );
};

export default BudgetManagement;
