import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const navigate = useNavigate();

  useEffect(() => {
    // Set body background color to black when the component mounts
    document.body.style.backgroundColor = '#000';

    // Clean up to reset background color when component unmounts
    return () => {
      document.body.style.backgroundColor = '';
    };
  }, []);

  const handleLogin = async (e) => {
    e.preventDefault();
    const email = e.target[0].value;
    const password = e.target[1].value;

    try {
      const res = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });

      const data = await res.json();

      if (res.ok) {
        localStorage.setItem('token', data.token);
        localStorage.setItem('userId', data.userId);
        navigate('/home');
      } else {
        alert(data.msg);
      }
    } catch (error) {
      console.error('Error during login:', error);
      alert('An error occurred while logging in. Please try again.');
    }
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', color: '#ffffff' }}>
      <div>
        <h2 className="text-center">Login</h2>
        <form onSubmit={handleLogin} style={{ display: 'flex', flexDirection: 'column', width: '400px', marginTop: '20px' }}>
          <input
            className="form-control"
            type="email"
            placeholder="Email"
            required
            style={{ marginBottom: '10px', backgroundColor: '#333', color: '#ffffff', border: '1px solid #555' }}
          />
          <input
            className="form-control"
            type="password"
            placeholder="Password"
            required
            style={{ marginBottom: '30px', backgroundColor: '#333', color: '#ffffff', border: '1px solid #555' }}
          />
          <button className="btn btn-primary" type="submit" style={{ width: '100px', margin: '0 auto', backgroundColor: '#1f1f1f', color: '#ffffff' }}>
            Login
          </button>
        </form>
        <p className="text-center mt-3">
          New user?{' '}
          <span onClick={() => navigate('/signup')} style={{ color: '#1e90ff', cursor: 'pointer' }}>
            Sign up here
          </span>
        </p>
      </div>
    </div>
  );
};

export default Login;
