import React, { useState } from 'react';
import { Line } from 'react-chartjs-2';
import { Pie } from 'react-chartjs-2';
import 'chart.js/auto'; // Make sure to import this for Chart.js version 3+
import './styles.css'; // Import your CSS styles

const ExpenseTracker = () => {
  const [expenses, setExpenses] = useState([]);
  const [date, setDate] = useState(new Date().toISOString().split('T')[0]); // Initial date is today
  const [totalExpense, setTotalExpense] = useState(0);
  const [dailyTotals, setDailyTotals] = useState([]);
  const [categoryTotals, setCategoryTotals] = useState({});
  const [category, setCategory] = useState('');
  const [amount, setAmount] = useState('');
  const [editedCategory, setEditedCategory] = useState('');
  const [editedAmount, setEditedAmount] = useState('');
  const [editIndex, setEditIndex] = useState(null);

  const handleAddExpense = () => {
    if (!amount || !category) return;

    const newExpense = { date, category, amount: parseFloat(amount) };
    setExpenses([...expenses, newExpense]);
    setTotalExpense((prev) => prev + parseFloat(amount));

    // Update daily totals and category totals
    updateTotals(newExpense);
    setAmount(''); // Clear the amount field
    setCategory(''); // Clear the category field
  };

  const updateTotals = (expense) => {
    const { date, category, amount } = expense;

    // Update daily totals
    const existingDateTotal = dailyTotals.find((item) => item.date === date);
    if (existingDateTotal) {
      existingDateTotal.total += amount;
    } else {
      dailyTotals.push({ date, total: amount });
    }
    setDailyTotals([...dailyTotals]);

    // Update category totals
    const existingCategoryTotal = categoryTotals[category] || 0;
    categoryTotals[category] = existingCategoryTotal + amount;
    setCategoryTotals({ ...categoryTotals });
  };

  const handleEditExpense = (index) => {
    const expenseToEdit = expenses[index];
    setEditedAmount(expenseToEdit.amount);
    setEditedCategory(expenseToEdit.category);
    setEditIndex(index);
  };

  const handleUpdateExpense = () => {
    if (editIndex !== null) {
      const updatedExpense = { ...expenses[editIndex], amount: parseFloat(editedAmount), category: editedCategory };
      const newExpenses = [...expenses];
      newExpenses[editIndex] = updatedExpense;
      setExpenses(newExpenses);
      setTotalExpense(newExpenses.reduce((acc, curr) => acc + curr.amount, 0));
      setEditIndex(null); // Clear edit index
      setEditedAmount(''); // Clear edited amount
      setEditedCategory(''); // Clear edited category
    }
  };

  const lineChartData = {
    labels: dailyTotals.map(item => item.date),
    datasets: [
      {
        label: 'Daily Total Expense',
        data: dailyTotals.map(item => item.total),
        fill: false,
        backgroundColor: 'rgba(75,192,192,1)',
        borderColor: 'rgba(75,192,192,1)',
      },
    ],
  };

  const pieChartData = {
    labels: Object.keys(categoryTotals),
    datasets: [
      {
        data: Object.values(categoryTotals),
        backgroundColor: [
          'rgba(255,99,132,0.2)',
          'rgba(54,162,235,0.2)',
          'rgba(255,206,86,0.2)',
          'rgba(75,192,192,0.2)',
          'rgba(153,102,255,0.2)',
        ],
        borderColor: [
          'rgba(255,99,132,1)',
          'rgba(54,162,235,1)',
          'rgba(255,206,86,1)',
          'rgba(75,192,192,1)',
          'rgba(153,102,255,1)',
        ],
        borderWidth: 1,
      },
    ],
  };

  return (
    <div className="container">
      <h2 className="text-center mb-4">Expense Tracker</h2>

      <div className="form-group">
        <label htmlFor="date">Select Date</label>
        <input
          type="date"
          id="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          className="form-control"
        />
      </div>

      <div className="form-group">
        <label htmlFor="amount">Amount</label>
        <input
          type="number"
          id="amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          className="form-control"
          placeholder="Enter amount"
        />
      </div>

      <div className="form-group">
        <label htmlFor="category">Category</label>
        <select
          id="category"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
          className="form-control"
        >
          <option value="">Select category</option>
          <option value="Food">Food</option>
          <option value="Accommodation">Accommodation</option>
          <option value="Travel">Travel</option>
          <option value="Other">Other</option>
        </select>
      </div>

      {category === 'Other' && (
        <div className="form-group">
          <label htmlFor="specific-category">Specify Category</label>
          <input
            type="text"
            id="specific-category"
            value={editedCategory}
            onChange={(e) => setEditedCategory(e.target.value)}
            className="form-control"
            placeholder="Enter specific category"
          />
        </div>
      )}

      <button onClick={handleAddExpense} className="btn btn-primary">Add Expense</button>
      <button onClick={handleUpdateExpense} className="btn btn-secondary ml-2">Update Expense</button>

      <h4 className="mt-4">Daily Expense Summary</h4>
      <ul className="list-group">
        {expenses.map((expense, index) => (
          <li key={index} className="list-group-item d-flex justify-content-between align-items-center">
            {expense.date} - {expense.category}: ${expense.amount}
            <button onClick={() => handleEditExpense(index)} className="btn btn-sm btn-warning">Edit</button>
          </li>
        ))}
      </ul>

      {dailyTotals.length > 0 && (
        <div className="chart-container mt-4">
          <h4 className="text-center">Spending Trend</h4>
          <Line data={lineChartData} options={{ maintainAspectRatio: false }} />
        </div>
      )}

      {Object.keys(categoryTotals).length > 0 && (
        <div className="chart-container mt-4">
          <h4 className="text-center">Category Spending Breakdown</h4>
          <Pie data={pieChartData} options={{ maintainAspectRatio: false }} />
        </div>
      )}
    </div>
  );
};

export default ExpenseTracker;
